import React, { useState } from 'react';
import { StyleSheet, TextInput, View } from 'react-native';
import ResultTableBase from './ResultTableBase';

function Cell({value = 0, index, onChange, rowName}) {

    const handleChange = val => {
      val = +val

      if (Number.isNaN(val)) return 
      console.log(val)
      if (!val) val = 0 
      onChange(rowName, index, val)
    }
    return (
      <View style={styles.cell}>
        <TextInput
            style={styles.cellText}
            name={index}
            value={value}
            keyboardType="numeric"
            onChangeText={handleChange}
        />
      </View>
    )
  }

export default function ResultTableForm() {
  const [state, setState] = useState({
    weights: [0],
    repetitions: [0]
  })
  const handleChangeCell = (key, index, val) => {
    console.log(key, index, val)

    setState(prevState => ({
      ...prevState, 
      [key]: state[key].map(
        (v, i) => i === index ? val : v
      )
    }))

    if (
      index === state[key].length - 1 &&
      (state.weights[state.weights.length-1] !== 0 && state.repetitions[state.repetitions.length-1] !== 0)
    ) {
      setState(prevState => ({
        weights: [...prevState.weights, 0],
        repetitions: [...prevState.repetitions, 0]
      }))
    }
  }

  return (
      <ResultTableBase
          data={state}
          color="white"
          bgColor="#d32f2f"
          renderCell={
            (value, index, rowName) => (
              <Cell 
                value={value} 
                index={index} 
                key={index} 
                onChange={handleChangeCell} 
                rowName={rowName} 
              />
          )}
      />
  )
}

  const styles = StyleSheet.create({
    cell: {
    },
    cellText: {
        lineHeight: 40,
        fontSize: 18,
        textAlign: 'center',
        color: 'white',

    }
  });
