import React from 'react';
import { StyleSheet, Text, View } from 'react-native';

function Row({title, items, renderCell}) {
  return (
    <View style={styles.tableRow}>
      <View style={styles.tableRowHeader}>
        <Text style={styles.tableRowHeaderText}>
          {title}
        </Text>
      </View>
      {items.map((val, index) => renderCell(val, index))}
    </View>
  )
}

export default function ResultTableBase({color, renderCell}) {
    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.headerTitle}>Date</Text>
        </View>
        <View style={styles.table}>
          <Row 
            title="Вес"
            items={[10, 10]}
            renderCell={renderCell}
          />
          <Row 
            title="Повторы"
            items={[10, 10]}
            renderCell={renderCell}
          />
        </View>
      </View>
    )
  }
  

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      borderColor: 'black',
      borderBottomWidth: 1,
    },
    header: {
      paddingBottom: 10,
    },
    headerTitle: {
      fontSize: 20,
      textAlign: 'center',
    },
    tableRow: {
      flexDirection: 'row',
      height: 40,
      borderColor: 'black',
      borderTopWidth: 1,
      borderLeftWidth: 1,
      borderRightWidth: 1,
    },
    tableRowHeader: {
      flexBasis: 90
    },
    tableRowHeaderText: {
      fontSize: 18,
      lineHeight: 30,
    }
  });
