import React from 'react';
import { StyleSheet, View, Text, FlatList } from 'react-native';

export default function TimerSetup() {
    return (
        <View>
            <Text style={styles.header}>Текущие настройки</Text>
            <FlatList 
                style={styles.list}
                keyExtractor={item => item.paramName}
                data={[
                    {paramName: 'Время тренировки', value: '00:45'},
                    {paramName: 'Время отдыха', value: '00:45'},
                    {paramName: 'Раунды', value: '3'}
                ]}
                renderItem={
                    ({item, index, seperators}) => <View style={styles.item}>
                        <Text style={styles.itemTitle}>{item.paramName}</Text>
                        <Text style={styles.itemValue}>{item.value}</Text>
                    </View>
                }
            />
        </View>
    )
}
const styles = StyleSheet.create({
    list: {
        borderBottomColor: "#efefef",
        borderBottomWidth: 1
    },
    header: {
        fontSize: 18,
        textAlign: 'center',
        padding: 7
    },
    item: {
        backgroundColor: 'white',
        borderTopColor: "#efefef",
        borderTopWidth: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: 40,
        paddingLeft: 10,
        paddingRight: 10
    },
    itemTitle: {
        fontSize: 16,
        color: "#464646",
    },
    itemValue: {
        fontSize: 18
    }
})
