import React from 'react';
import { StyleSheet, TouchableOpacity, View, Text } from 'react-native';

export default function Timer() {
    return (
        <View style={styles.container}>
            <View style={styles.leftSection}>
                <TouchableOpacity>
                   <Text style={styles.leftSectionText}>
                       Сбросить
                   </Text>
                </TouchableOpacity>
            </View>
            <View style={styles.middleSection}>
                <Text style={styles.middleSectionText}>22:00</Text>
                <Text style={styles.middleSectionText}>22:00</Text>
                <View style={styles.commonTime}>
                    <Text style={styles.commonTimeText}>22:00</Text>
                </View>
            </View>
            <View style={styles.rightSection}>
                <TouchableOpacity>
                    <Text style={styles.rightSectionText}>Старт</Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        borderWidth: 2,
        borderColor: '#d32f2f',
        height: 90
    },
    leftSection: {
        flex: 1,
        justifyContent: 'center',
    },
    leftSectionText: {
        textAlign: 'center',
        color: "#d32f2f",
        textTransform: 'uppercase',
        fontWeight: "700",
    },
    rightSection: {
        flex: 1,
        justifyContent: 'center',
    },
    rightSectionText: {
        textAlign: 'center',
        color: "#d32f2f",
        textTransform: 'uppercase',
        fontWeight: "700",
    },
    middleSection: {
        flex:2,
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        borderColor: '#d32f2f',
        borderRightWidth: 2,
        borderLeftWidth: 2,
        backgroundColor: "#d32f2f"
    },
    middleSectionText: {
        color: "white",
        fw
    },
    commonTime: {
        width: '100%',
    },
    commonTimeText: {
        textAlign: 'center',
        color: "white",
        textTransform: 'uppercase',
        fontWeight: "700",

    }
})
