import React, {useState} from 'react';
import { StyleSheet, View, Text, Pressable, Button } from 'react-native';
import { NumberInput, NumberKeyboard } from '../../../components';
import { Feather } from '@expo/vector-icons'; 
import { toMinSec } from '../../../utils';
import { useDispatch } from 'react-redux';
import { changeTimerParam } from '../timerSlice';

export default function valueInput({
    title,
    onCancel,
    onSubmit,
    paramName
}) {
    const [value, setValue] = useState('00');

    const dispatch = useDispatch()

    const handleSubmit = () => {
        dispatch(changeTimerParam({
            paramName,
            value: +value
        }))
        onSubmit()
    }

    return (
        <View styles={styles.container}>
            <View style={styles.title}>
                <Text style={styles.titleText}>
                    {title}
                </Text>
            </View>
            <View style={styles.minSec}>
                <Text style={styles.minSecText}>
                    {toMinSec(value)}
                </Text>
            </View>
            <NumberInput
                value={value}
            />
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        paddingTop: 150,
    },
    title: {
        borderBottomWidth: 1,
        padding: 10,
        alignItems: 'center'
    },
    titleText: {
        fontSize: 18,
        fontWeight: "700"
    },
    minSec: {
        padding: 20,
        alignItems: 'center'
    },
    minSecText: {
        fontSize: 18
    },
    sec: {
        width: 250,
        height: 70,
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
        alignSelf: 'center',
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#cbcaca',
    },
    secText: {
        fontSize: 50
    },
    buttons: {
        flexDirection: 'row',
        justifyContent: "space-around"
    },
    button: {
        width: 100
    }
})
