import React, { useState } from 'react';
import { StyleSheet, View, Text, FlatList, TouchableHighlight, Modal } from 'react-native';
import { useSelector } from 'react-redux';
import SecondsInput from './seconds-input';

export default function TimerSetup() {
    const [modalVisible, setModalVisible] = useState(false);
    const { restInterval, workInterval, rounds } = useSelector(state => state.timer);
    console.log(timer)
    return (
        <View>
            <Text style={styles.header}>Текущие настройки</Text>
            <FlatList 
                style={styles.list}
                keyExtractor={item => item.paramName}
                data={[
                    {paramName: 'Время тренировки', value: {}},
                    {paramName: 'Время отдыха', value: '00:45'},
                    {paramName: 'Раунды', value: '3'}
                ]}
                renderItem={
                    ({item, index, seperators}) => (
                    <TouchableHighlight
                        underlayColor="#e1e2e1"
                        onPress={() => setModalVisible(true)}
                    >
                        <View style={styles.item}>
                            <Text style={styles.itemTitle}>{item.paramName}</Text>
                            <Text style={styles.itemValue}>{item.value}</Text>
                        </View>
                    </TouchableHighlight>
                    )}
            />
            <Modal
                animationType="slide"
                transparent={false}
                visible={modalVisible}
                onRequestClose={() => {
                    setModalVisible(!modalVisible);
                }}
            >
                <View style={styles.modalView}>
                    <SecondsInput />
                </View>
            </Modal>
        </View>
    )
}

const styles = StyleSheet.create({
    list: {
        borderBottomColor: "#efefef",
        borderBottomWidth: 1
    },
    header: {
        fontSize: 18,
        textAlign: 'center',
        padding: 7
    },
    item: {
        backgroundColor: 'white',
        borderTopColor: "#efefef",
        borderTopWidth: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: 60,
        paddingLeft: 15,
        paddingRight: 15
    },
    itemTitle: {
        fontSize: 16,
        color: "#464646",
    },
    itemValue: {
        fontSize: 18
    },
    modalContainer: {
        width: '100%',
        height: '100%',
        backgroundColor: 'white',
    }
})
