import React from 'react';
import { StyleSheet, TouchableOpacity, View, Text } from 'react-native';
import { useNavigation } from '@react-navigation/core';
import { useSelector } from 'react-redux';

export default function Timer() {
    const navigation = useNavigation()
    const { workTime, restTime, rounds } = useSelector(state => state.timer)

    return (
        <View style={styles.container}>
            <View style={styles.leftSection}>
                <View style={styles.btnTop}>
                    <TouchableOpacity>
                        <Text style={styles.leftSectionText}>
                            Сбросить
                        </Text>
                    </TouchableOpacity>
                </View>
                <View style={styles.btnBottom}>
                    <TouchableOpacity onPress={() => navigation.navigate('Установка таймера')}>
                        <Text style={styles.rightSectionText}>
                            Изменить
                        </Text>
                    </TouchableOpacity>
                </View>
            </View>
            <View style={styles.middleSection}>
                <View style={styles.workTime}>
                    <Text style={[styles.workTimeText, styles.timeText]}>
                        <DateString value={workTime} />
                    </Text>
                </View>
                <View style={styles.restTime}>
                    <Text style={[styles.restTimeText, styles.timeText, styles.timeTextInactive]}>22:00</Text>
                </View>
                <View style={styles.commonTime}>
                    <Text style={[styles.commonTimeText, styles.timeText]}>22:00</Text>
                </View>
            </View>
            <View style={styles.rightSection}>
                <TouchableOpacity>
                    <Text style={styles.rightSectionText}>Старт</Text>
                </TouchableOpacity>
                
            </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        borderWidth: 2,
        borderColor: '#d32f2f',
        height: 80,
        alignItems: 'stretch',
        backgroundColor: "#d32f2f"
    },
    leftSection: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        borderRadius: 2,
        backgroundColor: "white",
        overflow: 'hidden'
    },
    leftSectionText: {
        textAlign: 'center',
        color: "#d32f2f",
        textTransform: 'uppercase',
        fontWeight: "700",
    },
    btnTop: {
        borderBottomWidth: 1,
        borderColor: "#d32f2f",
        width: '100%',
        height: '50%',
        justifyContent: 'center'
    },  
    btnBottom: {
        borderTopWidth: 1,
        borderColor: "#d32f2f",
        width: '100%',
        height: '50%',
        justifyContent: 'center',
    },
    rightSection: {
        flex: 1,
        justifyContent: 'center',
        borderRadius: 2,
        backgroundColor: "white",
        overflow: 'hidden'
    },
    rightSectionText: {
        textAlign: 'center',
        color: "#d32f2f",
        textTransform: 'uppercase',
        fontWeight: "700",
    },
    middleSection: {
        flex:2,
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        backgroundColor: "#d32f2f",
        alignItems: 'center',

    },
    timeText: {
        color: "white",
        fontWeight: '700',
        textAlign: 'center',
        fontSize: 20
    },
    timeTextInactive: {
        color: 'rgba(255, 255, 255, 0.6)'
    },
    workTime: {
        justifyContent: 'center',
        height: '50%'
    },
    workTimeText: {

    },
    restTime: {
        justifyContent: 'center'
    },
    restTimeText: {

    },
    commonTime: {
        width: '90%',
        justifyContent: 'center',
        height: "50%",
        borderTopWidth: 2,
        borderColor: "rgba(255, 255, 255, 0.3)"
    },
    commonTimeText: {

    }
})
