import React, { useState } from 'react';
import { StyleSheet, View, Text, FlatList, TouchableHighlight, Modal } from 'react-native';
import { useSelector } from 'react-redux';
import { toMinSec } from '../../utils';
import SecondsInput from './seconds-input';

export default function TimerSetup() {
    const { restInterval, workInterval, rounds } = useSelector(state => state.timer);
    const [modalVisibility, setModalVisibility] = useState({
        restInterval: false,
        workInterval: false,
        rounds: false
    });

    const timerParams = [
        {paramName: 'Время тренировки', value: toMinSec(workInterval), modalName: 'workInterval'},
        {paramName: 'Время отдыха', value: toMinSec(restInterval), modalName: 'restInterval'},
        {paramName: 'Раунды', value: rounds.toString(), modalName: 'rounds'}
    ]

    const showModal = name => {
        setModalVisibility({
            ...modalVisibility,
            [name]: true
        })
    }

    const hideModal = name => {
        setModalVisibility({
            ...modalVisibility,
            [name]: false
        })
    }

    return (
        <View>
            <Text style={styles.header}>Текущие настройки</Text>
            <FlatList 
                style={styles.list}
                keyExtractor={item => item.paramName}
                data={timerParams}
                renderItem={
                    ({item, index, seperators}) => (
                    <TouchableHighlight
                        underlayColor="#e1e2e1"
                        onPress={() => showModal(item.modalName)}
                    >
                        <View style={styles.item}>
                            <Text style={styles.itemTitle}>{item.paramName}</Text>
                            <Text style={styles.itemValue}>{item.value}</Text>
                        </View>
                    </TouchableHighlight>
                    )}
            />
            {timerParams.map(p => (
                <Modal
                    key={p.modalName}
                    animationType="slide"
                    transparent={false}
                    visible={modalVisibility[p.modalName]}
                    onRequestClose={() => {
                        hideModal(p.modalName);
                    }}
                >
                    <View style={styles.modalView}>
                        <SecondsInput 
                            title={p.paramName} 
                            onCancel={() => hideModal(p.modalName)}
                            onSubmit={() => handleSubmit}
                        />
                    </View>
                </Modal>
            ))}
        </View>
    )
}

const styles = StyleSheet.create({
    list: {
        borderBottomColor: "#efefef",
        borderBottomWidth: 1
    },
    header: {
        fontSize: 18,
        textAlign: 'center',
        padding: 7
    },
    item: {
        backgroundColor: 'white',
        borderTopColor: "#efefef",
        borderTopWidth: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: 60,
        paddingLeft: 15,
        paddingRight: 15
    },
    itemTitle: {
        fontSize: 16,
        color: "#464646",
    },
    itemValue: {
        fontSize: 18
    },
    modalContainer: {
        width: '100%',
        height: '100%',
        backgroundColor: 'white',
    }
})
