import React, {useState} from 'react';
import { StyleSheet, View, Text, Pressable, Button } from 'react-native';
import { NumberKeyboard } from '../../../components';
import { Feather } from '@expo/vector-icons'; 
import { toMinSec } from '../../../utils';
import { useDispatch } from 'react-redux';
import { changeTimerParam } from '../timerSlice';

export default function SecondsInput({
    title,
    onCancel,
    onSubmit,
    paramName
}) {
    const [seconds, setSeconds] = useState('00');

    const prepend = (string, char) => {
        const chars = string.split('')
        chars.push(char)
        return chars.slice(-2).join("")
    }

    const append = (string, char) => {
        const chars = string.split('')
        chars.unshift(char)
        return chars.slice(0, 2).join("")
    }

    const dispatch = useDispatch()

    const handleSubmit = () => {
        dispatch(changeTimerParam({
            paramName,
            value: +seconds
        }))
        
    }

    return (
        <View styles={styles.container}>
            <View style={styles.title}>
                <Text style={styles.titleText}>
                    {title}
                </Text>
            </View>
            <View style={styles.minSec}>
                <Text style={styles.minSecText}>
                    {toMinSec(seconds)}
                </Text>
            </View>
            <View style={styles.sec}>
                <Text style={styles.secText}>
                   {seconds} 
                </Text>
                <View>
                    <Pressable onPress={() => setSeconds(seconds => append(seconds, '0'))}>
                        <Feather name="delete" size={40} color="black" />
                    </Pressable>
                </View>
            </View>
            <NumberKeyboard onKeyPress={(k) => setSeconds(seconds => prepend(seconds, k))}/>
            <View style={styles.buttons}>
                <View style={styles.button}>
                    <Button color="#d3d3d3" title="Отмена" onPress={onCancel}/>
                </View>
                <View style={styles.button} >
                    <Button style={styles.button} title="Ок" onPress={handleSubmit}/>
                </View>
            </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        paddingTop: 150,
    },
    title: {
        borderBottomWidth: 1,
        padding: 10,
        alignItems: 'center'
    },
    titleText: {
        fontSize: 18,
        fontWeight: "700"
    },
    minSec: {
        padding: 20,
        alignItems: 'center'
    },
    minSecText: {
        fontSize: 18
    },
    sec: {
        width: 250,
        height: 70,
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
        alignSelf: 'center',
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#cbcaca',
    },
    secText: {
        fontSize: 50
    },
    buttons: {
        flexDirection: 'row',
        justifyContent: "space-around"
    },
    button: {
        width: 100
    }
})
