import { createSlice } from '@reduxjs/toolkit';

const timerSlice = createSlice({
    name: 'timer',
    initialState: {
        workInterval: 45,
        restInterval: 40,
        rounds: 3
    },
    reducers: {
        changeTimerParam(state, action) {
            console.log(state[action.paramName])
            const { paramName, value } = action;
            state[paramName] = value;
            return state
        }
    }
})

export const { changeTimerParam } = timerSlice.actions;

export default timerSlice.reducer;
