import React, {useState} from 'react';
import { StyleSheet, View, Text, Pressable } from 'react-native';
import { NumberKeyboard } from '../../../components';
import { Feather } from '@expo/vector-icons'; 

export default function SecondsInput() {
    const [seconds, setSeconds] = useState('00');

    const prepend = (string, char) => {
        const chars = string.split('')
        chars.push(char)
        return chars.slice(-2).join("")
    }

    const append = (string, char) => {
        const chars = string.split('')
        chars.unshift(char)
        return chars.slice(0, 2).join("")
    }


    return (
        <View styles={styles.container}>
            <View style={styles.minSec}>
                <Text style={styles.minSecText}>
                    {toMinSec(seconds)}
                </Text>
            </View>
            <View style={styles.sec}>
                <Text style={styles.secText}>
                   {seconds} —Å
                </Text>
                <View>
                    <Pressable onPress={() => setSeconds(seconds => append(seconds, '0'))}>
                        <Feather name="delete" size={40} color="black" />
                    </Pressable>
                </View>
            </View>
            <NumberKeyboard onKeyPress={(k) => setSeconds(seconds => prepend(seconds, k))}/>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        paddingTop: 150,
    },
    minSec: {
        padding: 20,
        alignItems: 'center'
    },
    minSecText: {
        fontSize: 18
    },
    sec: {
        width: 250,
        height: 70,
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
        alignSelf: 'center',
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#cbcaca',
    },
    secText: {
        fontSize: 50
    }
})
