import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { Table, TableWrapper, Row, Rows, Col } from '@deb-95/react-native-table-component';

export default function ResultTableBase({
  data,
  bgColor, 
  color, 
  renderCell = null
}) {
    const tableData = [
      [0, 0],
      [0, 0]
    ]

    const twoDimensionalArray = Object.values(data)
    console.log(twoDimensionalArray)

    let rows;

    if (renderCell) {
      const rowNames = [
        'weights',
        'repetitions'
      ]
      rows = twoDimensionalArray.map(
        (row, rowIndex) => row.map(
          (val, cellIndex) => renderCell(val, cellIndex, rowNames[rowIndex])
        )
      )
    } else {
      rows = twoDimensionalArray
    }

    return (
      <View style={[
        styles.container,
        {backgroundColor: bgColor}
      ]}>
        <View style={styles.header}>
          <Text style={{
            ...styles.headerTitle,
            color
          }}>Date</Text>
        </View>

        <Table  style={styles.table} borderStyle={{borderWidth: 1, borderColor: color}}>
          <TableWrapper style={styles.tableWrapper}>
            <Col 
              data={['Вес', 'Повторы']} 
              style={styles.tableRowTitle} 
              heightArr={[40, 40]} 
              textStyle={[styles.tableRowTitleText, {color}]}
            />
            <Rows data={rows} widthArr={[50, 50]} heightArr={[40, 40]} style={styles.tableRow} textStyle={styles.tableRowText} />
          </TableWrapper>
        </Table>
      </View>
    )
  }
  

const styles = StyleSheet.create({
    container: {
      backgroundColor: '#fff',
      paddingTop: 15,
      paddingBottom: 15,
      paddingLeft: 15,
      paddingRight: 15,
    },
    header: {
      paddingBottom: 10,
      paddingTop: 10,
    },
    headerTitle: {
      fontSize: 20,
      textAlign: 'center',
    },
    table: {
      width: 210
    },
    tableWrapper: {
      flexDirection: 'row',
    },
    tableRow: {

    },
    tableRowTitle: {
      width: 150

    },
    tableRowTitleText: {
      fontSize: 18,
      lineHeight: 40,
      padding: 5    
    }
  });
