import React, { useState } from 'react';
import { StyleSheet, View, Text, FlatList, TouchableHighlight, Modal } from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import NumberPlease from "react-native-number-please";

export default function TimerSetup() {
    const [modalVisible, setModalVisible] = useState(false)
    const [show, isShow] = useState(false)

    const initialValues = [{ id: "pizza", value: 3 }];
    const [pizzas, setPizzas] = useState(initialValues);
    const pizzaNumbers = [{ id: "pizza", label: "üçï", min: 0, max: 99 }];
  
    return (
        <View>
            <Text style={styles.header}>–¢–µ–∫—É—â–∏–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏</Text>
            <FlatList 
                style={styles.list}
                keyExtractor={item => item.paramName}
                data={[
                    {paramName: '–í—Ä–µ–º—è —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏', value: '00:45'},
                    {paramName: '–í—Ä–µ–º—è –æ—Ç–¥—ã—Ö–∞', value: '00:45'},
                    {paramName: '–†–∞—É–Ω–¥—ã', value: '3'}
                ]}
                renderItem={
                    ({item, index, seperators}) => (
                    <TouchableHighlight
                        underlayColor="#e1e2e1"
                        onPress={() => isShow(true)}
                    >
                        <View style={styles.item}>
                            <Text style={styles.itemTitle}>{item.paramName}</Text>
                            <Text style={styles.itemValue}>{item.value}</Text>
                        </View>
                    </TouchableHighlight>
                    )}
            />
            <Modal
                animationType="fade "
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => {
                    setModalVisible(!modalVisible);
                }}
            >
                <View style={styles.modalContainer}>
                    <View style={styles.modalView}>
                        <View style={styles.modalHeader}>
                            <Text style={styles.modalHeaderView}>–®–∞–ø–∫–∞</Text>
                        </View>
                    </View>
                </View>
            </Modal>

            {/* {
                show&&<DateTimePicker
                    value={new Date(2021, 6, 28, 0, 0, 30)}
                    mode="time"
                    onDateChange={(v) => console.log(v)}
                />
            } */}

            {/* <NumberPlease
                    digits={pizzaNumbers}
                    values={pizzas}
                    onChange={(values) => setPizzas(values)}
                /> */}
        </View>
    )
}
const styles = StyleSheet.create({
    list: {
        borderBottomColor: "#efefef",
        borderBottomWidth: 1
    },
    header: {
        fontSize: 18,
        textAlign: 'center',
        padding: 7
    },
    item: {
        backgroundColor: 'white',
        borderTopColor: "#efefef",
        borderTopWidth: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: 60,
        paddingLeft: 15,
        paddingRight: 15
    },
    itemTitle: {
        fontSize: 16,
        color: "#464646",
    },
    itemValue: {
        fontSize: 18
    },
    modalContainer: {
        flex: 1,
        backgroundColor: 'rgba(0, 0, 0, 0.3)',
        justifyContent: 'center',
        alignItems: 'center'
    },
    modalView: {
        width: 320,
        height: 220,
        backgroundColor: 'white',
    }
})
