import React from 'react';
import { StyleSheet, Text, View, YellowBox } from 'react-native';
import { Table, TableWrapper, Row, Rows, Col } from '@deb-95/react-native-table-component';

export default function ResultTableBase({
  bgColor, 
  color, 
  renderCell = null
}) {
    const tableData = [
      [0, 0],
      [0, 0]
    ]

    let cells;

    if (renderCell) {
      cells = tableData.map(val => renderCell())
    } else {

    }

    return (
      <View style={{
        ...styles.container,
        backgroundColor: bgColor
      }}>
        <View style={styles.header}>
          <Text style={{
            ...styles.headerTitle,
            color
          }}>Date</Text>
        </View>

        <Table  borderStyle={{borderWidth: 1}}>
          <TableWrapper style={{flexDirection: 'row'}}>
            <Col data={['Вес', 'Повторы']} style={styles.tableRowTitle} heightArr={[40, 40]} textStyle={styles.tableRowTitleText}/>
            <Rows data={tableData} widthArr={[50, 50]} heightArr={[40, 40]} style={styles.tableRow} textStyle={styles.tableRowText} />
          </TableWrapper>
        </Table>
      </View>
    )
  }
  

const styles = StyleSheet.create({
    container: {
      backgroundColor: '#fff',
      paddingLeft: 1,
      paddingRight: 1,
      height: 250
    },
    header: {
      paddingBottom: 10,
      paddingTop: 10,
    },
    headerTitle: {
      fontSize: 20,
      textAlign: 'center',
    },
    tableBorder: {
      borderWidth: 1,
      borderColor: '#ddd'
    },
    tableRow: {

    },
    tableRowText: {
      textAlign: 'center'
    },
    tableRowTitle: {
      width: 150

    },
    tableRowTitleText: {
      fontSize: 18,
      lineHeight: 40,
      padding: 5    
    }
  });
